 <!DOCTYPE html> <html>	<head>		<meta charset = "UTF-8"/>		<title>Dackelklicker</title>		<link rel = "stylesheet" href = "dackelklickerCss.css">	</head>	<body>	<script>	var w = window.innerWidth*0.9+"px";	var h = window.innerHeight*0.9+"px";	var x = null;	var y = null;	var variableNachrichtResetZaehler = 0;	var variableNachrichtAusgabeNachricht = "";	var vairablePixel = 0;	var variablePausierer = 300;	var variableWueffe = 0;	var variableDackel = 0;	var variableDackelPreis = 0;	var variableDackelSchuh = 0;	var variableDackelSchuhPreis = 10;	var variableDoerfer = 0;	var variableDoerferPreis = 100;	//browsercheck	var variableFirefox = typeof InstallTrigger !== 'undefined';	var variableChrome = !!window.chrome && (!!window.chrome.webstore || !!window.chrome.runtime);	</script>	<?php 	include "Datenbank.php";	$statementNutzersuche = "SELECT * FROM Dackelspiel WHERE DackelspielNutzerID = ".$_SESSION['NutzerID'].";";	$integerino = $connect->prepare($statementNutzersuche);	$integerino->execute();	$integerino2 = $integerino->fetchAll(PDO::FETCH_ASSOC);	$integerino->closeCursor();	if($integerino2){		foreach($connect->query($statementNutzersuche)as $eintrag){		echo 	'<script>		variablePausierer = 300;		variableWueffe = '.$eintrag['Wueffe'].';		variableDackel = '.$eintrag['Dackel'].';		vairablePixel = '.$eintrag['Pixel'].';		variableDackelPreis = 10*'.$eintrag['Dackel'].';		variableDackelSchuh = '.$eintrag['DackelSchuhe'].';		variableDackelSchuhPreis = 10;		variableDoerfer = '.$eintrag['Dackelhorte'].';		variableDoerferPreis = 100*(variableDoerfer+1);		</script>';	}	}else{		$statementErstelleDurchlauf = "insert into Dackelspiel(Wueffe, Dackel, DackelSchuhe, Dackelhorte, DackelspielNutzerID, Pixel) VALUES('0','0','0','0','".$_SESSION['NutzerID']."', '0');";		$integerino = $connect->prepare($statementErstelleDurchlauf);		$integerino->execute();	}	?>	<div class = "table">		<div class = "tr">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" value = "Deine Wüffe: ">			</div>			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" id = "textfeldWueffe">			</div>		</div>		<div class = "tr">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" value = "Deine Dackel: ">			</div>			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" id = "textfeldDackel">			</div>		</div>		<div class = "tr">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" value = "Dackelpreis: ">			</div>			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" id = "textfeldDackelPreis">			</div>			<div class = "td">				<input type = "button" class = "buttonDackeln" id = "button" value = "kaufen" onClick = "kaufeDackel()">			</div>		</div>		<div class = "tr">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" value = "Dackelschuhpaare: ">			</div>			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" id = "textfeldDackelSchuh">			</div>		</div>		<div class = "tr">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" value = "Paarpreis: ">			</div>			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" id = "textfeldDackelSchuhPreis">			</div>			<div class = "td">				<input type = "button" class = "buttonDackeln" id = "button" value = "kaufen" onClick = "kaufeDackelSchuh()">			</div>		</div>		<div class = "tr">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" value = "Deine Dackelhorte: ">			</div>			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" id = "textfeldDorf">			</div>		</div>		<div class = "tr">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" value = "Dackelhort Preis: ">			</div>			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarDackeln" id = "textfeldDorfPreis">			</div>			<div class = "td">				<input type = "button" class "buttonDackeln" id = "button" value = "kaufen" onClick = "kaufeDackelHorte()">			</div>		</div>		<div class = "tr">			<div class = "td">				<div class = "startButtonDiv">					<input type = "button" class = "buttonStart" id = "buttonStart" value = "Starten" onClick = "start()">				</div>				<form action = "speichern.php" method = "POST" class = "speichernButtonFeld">					<input type = "textfield" class = "textFeldUnsichtbar" id = "textfeldPausiererUnsichtbar" name =  "textfeldPausiererUnsichtbar">					<input type = "textfield" class = "textFeldUnsichtbar" id = "textfeldWueffeUnsichtbar" name = "textfeldWueffeUnsichtbar">					<input type = "textfield" class = "textFeldUnsichtbar" id = "textfeldDackelUnsichtbar" name = "textfeldDackelUnsichtbar">					<input type = "textfield" class = "textFeldUnsichtbar" id = "textfeldDackelPreisUnsichtbar" name = "textfeldDackelPreisUnsichtbar">					<input type = "textfield" class = "textFeldUnsichtbar" id = "textfeldDackelSchuhUnsichtbar" name = "textfeldDackelSchuhUnsichtbar">					<input type = "textfield" class = "textFeldUnsichtbar" id = "textfeldDackelSchuhPreisUnsichtbar" name = "textfeldDackelSchuhPreisUnsichtbar">					<input type = "textfield" class = "textFeldUnsichtbar" id = "textfeldDoerferUnsichtbar" name = "textfeldDoerferUnsichtbar">					<input type = "textfield" class = "textFeldUnsichtbar" id = "textfeldDoerferPreisUnsichtbar" name = "textfeldDoerferPreisUnsichtbar">					<input type = "textfield" class = "textFeldUnsichtbar" id = "textfeldPixelUnsichtbar" name = "textfeldPixelUnsichtbar">					<input type = "submit" class = "buttonSpeichern" id = "buttonSpeichern" value = "speichern">				</form>			</div>		</div>		<div class = "tr">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarNachricht" id = "textfeldUnklickbarNachricht" value = "NaN">			</div>		</div>
		<div class = "trNoBorder">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarAnleitung" id = "textfeldUnklickbarAnleitung">			</div>		</div>		<div class = "trNoBorder">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarAnleitung" id = "textfeldUnklickbarAnleitung2">			</div>		</div>		<div class = "trNoBorder">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarAnleitung" id = "textfeldUnklickbarAnleitung3">			</div>		</div>		<div class = "trNoBorder">			<div class = "td">				<input type = "textfield" class = "textfeldUnklickbarAnleitung" id = "textfeldUnklickbarAnleitung4">			</div>		</div>
	</div>	<img src = "chromusSuckus.png" id = "chromeBild">	<script>		var bildChrome = document.getElementById("chromeBild");		var feldNachricht = document.getElementById("textfeldUnklickbarNachricht");		var feldAnleitung = document.getElementById("textfeldUnklickbarAnleitung");		var feldAnleitung2 = document.getElementById("textfeldUnklickbarAnleitung2");		var feldAnleitung3 = document.getElementById("textfeldUnklickbarAnleitung3");		var feldAnleitung4 = document.getElementById("textfeldUnklickbarAnleitung4");		var feldPausiererUnsichtbar = document.getElementById("textfeldPausiererUnsichtbar");		var feldWueffe = document.getElementById("textfeldWueffe");		var feldWueffeUnsichtbar = document.getElementById("textfeldWueffeUnsichtbar");		var feldDackel = document.getElementById("textfeldDackel");		var feldDackelUnsichtbar = document.getElementById("textfeldDackelUnsichtbar");		var feldDackelPreis = document.getElementById("textfeldDackelPreis");		var feldDackelPreisUnsichtbar = document.getElementById("textfeldDackelPreisUnsichtbar");		var feldDackelSchuh = document.getElementById("textfeldDackelSchuh");		var feldDackelSchuhUnsichtbar = document.getElementById("textfeldDackelSchuhUnsichtbar");		var feldDackelSchuhPreis = document.getElementById("textfeldDackelSchuhPreis");		var feldDackelSchuhPreisUnsichtbar = document.getElementById("textfeldDackelSchuhPreisUnsichtbar");		var feldDorf = document.getElementById("textfeldDorf");		var feldDorfUnsichtbar = document.getElementById("textfeldDoerferUnsichtbar");		var feldDorfPreis = document.getElementById("textfeldDorfPreis");		var feldDorfPreisUnsichtbar = document.getElementById("textfeldDoerferPreisUnsichtbar");		var feldPixel = document.getElementById("textfeldPixelUnsichtbar");	</script>	<script>		document.addEventListener('mousemove', onMouseUpdate, false);		document.addEventListener("keydown", event => {		if(event.keyCode == 49)		{			kaufeDackel();		}else if(event.keyCode == 50)		{			kaufeDackelSchuh();		}else if(event.keyCode == 51)		{			kaufeDackelHorte();		}else if(event.keyCode == 52)		{			var felderEventCode52 = document.getElementsByClassName("textfeldUnklickbarAnleitung");			if(felderEventCode52[0].style.display != "none"){			feldAnleitung.style.display = "none";			feldAnleitung2.style.display = "none";			feldAnleitung3.style.display = "none";			feldAnleitung4.style.display = "none";			}else if(felderEventCode52[0].style.display == "none"){				feldAnleitung.style.display = "block";				feldAnleitung2.style.display = "block";				feldAnleitung3.style.display = "block";				feldAnleitung4.style.display = "block";			}		}	});	function nachrichtAusgabe(zeichenkette, boolIstAusgabe){		variableNachrichtResetZaehler++;		if(boolIstAusgabe == 1)		{			var audioError = new Audio('errorsound.mp3');			variableNachrichtResetZaehler = 0;			feldNachricht.style.color = "red";			feldNachricht.value = zeichenkette;			audioError.play();		}else{feldNachricht.value = zeichenkette;			feldNachricht.style.color = "green";			variableNachrichtResetZaehler = 0;		}	}	function onMouseUpdate(e){		x = e.pageX;		y = e.pageY;			vairablePixel++;		bildChrome.style.top =  y-(bildChrome.height/2)+"px";		bildChrome.style.left = x-(bildChrome.width/2)+"px";		//wenn chrome verwendet wird				if(variableChrome){			bildChrome.style.display = "block";		}	}	function punkteErhoehen(){		variableWueffe = variableWueffe+(1+variableDackel+(variableDoerfer*10));		feldWueffe.value = variableWueffe;	}	function kaufeDackel(){		switch (true){			case variableWueffe <= variableDackelPreis:				nachrichtAusgabe("zu wenige Wüffe", 1);			break;			case variableWueffe >= variableDackelPreis:				variableWueffe = variableWueffe - variableDackelPreis;				variableDackel = variableDackel+1;				variableDackelPreis = (variableDackelPreis+10);			break;		}	}	function kaufeDackelSchuh(){		switch (true){			case variableDackelSchuh >= variableDackel:				nachrichtAusgabe("bereits ausreichende schuhe!", 1);			break;			case variableDackelSchuh <= variableDackel:				if(variableWueffe >=10)				{					variableWueffe = variableWueffe - variableDackelSchuhPreis;					variableDackelSchuh = variableDackelSchuh+1;				break;				}else				{					feldNachricht.value = "zu wenige wüffe";				}		}	}	function kaufeDackelHorte(){		switch (true){			case variableDackel <= variableDoerferPreis:				nachrichtAusgabe("zu wenige Dackel", 1);			break;			case variableDackel >= variableDoerferPreis:				variableDackel = variableDackel - variableDoerferPreis;				variableDackelPreis = 10*variableDackel;				if((variableDackelSchuh-variableDoerferPreis) >=0){					variableDackelSchuh = variableDackelSchuh - variableDoerferPreis;				}else{					variableDackelSchuh = 0;				}					variableDoerfer = variableDoerfer+1;					variableDoerferPreis = ((variableDoerfer+1)*100);				break;		}	}	function ausgabe(){		feldPausiererUnsichtbar.value = variablePausierer;		feldWueffe.value = variableWueffe;		feldWueffeUnsichtbar.value = variableWueffe;		feldDackel.value = variableDackel;		feldDackelUnsichtbar.value = variableDackel;		feldDackelPreis.value = variableDackelPreis;		feldDackelPreisUnsichtbar.value = variableDackelPreis;		feldDorf.value = variableDoerfer;		feldDorfUnsichtbar.value = variableDoerfer;		feldDorfPreis.value = variableDoerferPreis;		feldDorfPreisUnsichtbar.value = variableDoerferPreis;		feldDackelSchuh.value = variableDackelSchuh;		feldDackelSchuhUnsichtbar.value = variableDackelSchuh;		feldDackelSchuhPreis.value = variableDackelSchuhPreis;		feldDackelSchuhPreisUnsichtbar.value = variableDackelSchuhPreis;		feldPixel.value = vairablePixel;		nachrichtAusgabe("NaN", 0);	}	function loop(){		punkteErhoehen();		ausgabe();		if((variablePausierer-(variableDackelSchuh)/100)<=30){			setTimeout(loop, 30);		}else{			setTimeout(loop, variablePausierer-(variableDackelSchuh)/100);		}	}	function start(){		feldAnleitung.value = 	"1. dackel kaufen";		feldAnleitung2.value = 	"2. Dackelschuhpaar Kaufen";		feldAnleitung3.value = 	"3. Dackelhort kaufen";		feldAnleitung4.value = 	"4. Anleitung Ein/Ausblenden";		var felderEventCode52 = document.getElementsByClassName("textfeldUnklickbarAnleitung");		for(var i = 0; i<felderEventCode52.length;i++){			felderEventCode52[i].style.display = "block";		}		var knopf = document.getElementById("buttonStart");		knopf.style.display = "none";		knopf.id = 'farmen';		loop();		var knopfSpeichern = document.getElementById("buttonSpeichern");		knopfSpeichern.style.display = "block";	}	</script>	</body></html>